import React, { useState } from 'react';

/*Desafio 2 - Exercício 4*/
function App() {

  const contactArray = [
    { name: 'Diego' },
    { name: 'Gabriel' },
    { name: 'Lucas' }
  ]

  class NameForm extends React.Component {
    constructor(props) {
      super(props);
      this.state = { contacts: contactArray };

      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(event) {
      e.preventDefault();
      const { contacts } = this.state,
        name = this.this.refs.name.value;
      this.setState({
        contacts: [...contacts, { name }]
      }, () => { this.refs.name.value = '' });
    }
    render() {
      const { contacts } = this.state;

      return (
        <div>
          <h2>Add Someone</h2>
          <form onSubmit={this.handleSubmit}>
            <input type="text" ref="name" placeholder="name" />
            <input type="text" ref="email" placeholder="email" />
            <input type="text" ref="phone" placeholder="phone" />
            <button type="submit">Submit</button>
          </form>
          <h2>Exsiting contacts:</h2>
          <ul>
            {contacts.map((contact) =>
              <li>{`Name: ${contact.name} Email: ${contact.email} Phone: ${contact.phone}`}</li>
            )}
          </ul>
        </div>
      )
    }
  }
React.DOM.render(<NameForm/>,document.getElementById('root'));
  return (
    <NameForm>

    </NameForm>
  )
}

export default App;
